#include <minishell.h>
#include <libft.h>

int ft_env(char **envp)
{
    int i;

    i = 0;
    while (envp[i])
    {
        ft_putstr_fd(envp[i], 1);
        write(1, "\n", 1);
        i++;
    }
    return (0);
}

